import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../sass/ShowStyle/showstyle.css";
import knife from "../img/box-chat/knife.png";

const ShowList = () => {
  const [showList, setShowList] = useState([]);
  // const [showDescription, setShowDescription] = useState("");

  useEffect(() => {
    getListData();
  }, []);

  const getListData = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/get-all-list/`);
      if (res.status === 200) {
        setShowTitle(res.data);
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="w-full h-full">
      <div className="relative flex mt-12 px-32 mb-0 py-2">
        <div className="grid grid-cols-3 gap-12">
          <ListCardLoop />
        </div>
      </div>
    </div>
  );
};

const CardList = () => {
  const [showImage, setShowImage] = useState(false);

  const handleClick = (e) => {
    e.preventDefault();
    setShowImage(!showImage);
  };
  return (
    <div className="relative">
      <div className="show-title1 banner">
        <div className="cs-title">
          {showTitle.map((data, index) => (
            <span key={index.title} className="s-title-txt">
              {data.title}
            </span>
          ))}
        </div>
      </div>
      <img
        className={`pin ${showImage ? "show" : "hide"}`}
        src={knife}
        alt="Knife"
      />
      <div className="show-title2" onClick={handleClick}>
        <div className="cs-title">
          <a href="#" className="s-title-txt">
            P<span className="bg-single-word">i</span>n
          </a>
        </div>
      </div>
      <div className="show-title3">
        <div className="cs-title">
          <a href="#" className="s-title-txt">
            Up<span className="bg-single-word">D</span>ate
          </a>
        </div>
      </div>
      <div className="show-title4">
        <div className="cs-title">
          <a href="#" className="s-title-txt">
            De<span className="bg-single-word">L</span>ete
          </a>
        </div>
      </div>
      <div className="box_todo_list">
        <svg className="phone-svg">
          <polygon points="10,0 300,0 290,100 290,310 5,310" />
        </svg>
        <svg className="screen-border-svg1">
          <polygon points="20,10 280,10 280,300 250,300 20,302" />
        </svg>
        <svg className="screen-border-svg2">
          <polygon points="25,14 270,18 265,297 23,297" />
        </svg>
        <span className="max-w-[230px] max-h-[220px] bg-transparent absolute z-50 top-12 left-8 text-white overflow-auto scrollbar-none">
          {showDescription}
        </span>
      </div>
    </div>
  );
};
const ListCardLoop = () => {
  {
    data.map((item) => (
      <CardList
        key={item.id}
        title={item.title}
        description={item.description}
      />
    ));
  }
  const loop = 1;
  return Array.from({ length: loop }, (_, index) => <CardList key={index} />);
};

export default ShowList;
