import React, { useState } from "react";
import axios from "axios";
import "../sass/todolist.css";
import img_box from "../img/d/profil.png";
import box_chat from "../img/box-chat/db-main-small.png";
import knife from "../img/box-chat/knife.png";

const ListToDo = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const fetchData = async (e) => {
    e.preventDefaults();
    try {
      const res = await axios.post("http://localhost:6000/api/create-list", {
        title
      });
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="bg_list">
      <div className="box_todo_list">
        <svg class="phone-svg">
          <polygon points="10,0 300,0 290,300 290,450 0,450" />
        </svg>
        <svg class="screen-border-svg">
          <polygon points="20,10 280,10 270,300 270,440 10,440" />
        </svg>
      </div>
      <div className="title-list">
        <div class="todolist">
          <span class="lettering-under">ToDoList</span>
          <span class="lettering">
            <span className="color">T</span>odo
            <span className="color">L</span>ist
          </span>
        </div>
        <svg class="zig-shadow">
          <polygon points="70,90  80,90  60,170  110,230  80,290  50,290  70,225  30,170" />
        </svg>
        <svg class="zig">
          <polygon points="70,80  80,80  60,160  110,220  80,280  50,280  70,215  30,160" />
        </svg>
        <div className="todo">
          <div className="profil">
            <svg class="borders-shadow">
              <polygon points="6,10 80,20 110,70 20,92" />
            </svg>
            <svg class="borders">
              <polygon points="0,10 80,20 110,75 20,95" />
            </svg>
            <svg class="outlines">
              <polygon points="10,10 80,13 110,63 25,80" />
            </svg>
            <form action="">
              <div className="text-container">
                <img className="text-box" src={box_chat} alt="" />
                <input
                  className="text-input1"
                  type="text"
                  placeholder="title"
                />
                <textarea className="text-input2" placeholder="your List" />
              </div>
              <div className="wrap-btn">
                <input className="submit" type="submit" id="bg-back" />
                <img className="knfu-img" src={knife} alt="" />
              </div>
            </form>
          </div>
          <img className="img-imagine" src={img_box} width={100} alt="" />
        </div>
      </div>
    </div>
  );
};

export default ListToDo;
